const weather = $input.first().json;
const currentCondition = weather.current_condition[0];
const location = weather.nearest_area[0];

return {
  location: {
    city: "Amsterdam",
    country: location.country[0].value,
    region: location.region[0].value
  },
  current: {
    temperature_celsius: currentCondition.temp_C,
    temperature_fahrenheit: currentCondition.temp_F,
    feels_like_celsius: currentCondition.FeelsLikeC,
    feels_like_fahrenheit: currentCondition.FeelsLikeF,
    weather_description: currentCondition.weatherDesc[0].value,
    humidity: currentCondition.humidity,
    wind_speed_kmph: currentCondition.windspeedKmph,
    wind_direction: currentCondition.winddir16Point,
    pressure_mb: currentCondition.pressure,
    visibility_km: currentCondition.visibility,
    uv_index: currentCondition.uvIndex,
    cloudcover: currentCondition.cloudcover
  },
  timestamp: new Date().toISOString()
};
