{
  "title": "Person Schema {{$timestamp}}",
  "description": "Schema for Newman testing",
  "organisation": "{{org_uuid}}",
  "properties": {
    "name": {"type": "string", "required": true},
    "age": {"type": "integer"},
    "active": {"type": "boolean", "default": true}
  }
}

{
  "title": "Validation Test Schema {{$timestamp}}",
  "description": "Comprehensive schema to test all validation rules including type and format",
  "organisation": "{{org_uuid}}",
  "hardValidation": true,
  "properties": {
    "required_string": {
      "type": "string",
      "required": true,
      "description": "A required string field"
    },
    "string_with_length": {
      "type": "string",
      "minLength": 3,
      "maxLength": 20,
      "description": "String must be between 3 and 20 characters"
    },
    "string_with_pattern": {
      "type": "string",
      "pattern": "^[A-Z]{2,3}-[0-9]{4}$",
      "description": "String must match pattern like AB-1234 or ABC-5678"
    },
    "enum_field": {
      "type": "string",
      "enum": ["draft", "published", "archived"],
      "description": "Must be one of: draft, published, archived"
    },
    "integer_with_range": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100,
      "description": "Integer between 0 and 100"
    },
    "number_with_range": {
      "type": "number",
      "minimum": 0.0,
      "maximum": 1.0,
      "description": "Decimal number between 0.0 and 1.0"
    },
    "array_with_items": {
      "type": "array",
      "minItems": 1,
      "maxItems": 5,
      "items": {
        "type": "string"
      },
      "description": "Array must have 1-5 string items"
    },
    "boolean_field": {
      "type": "boolean",
      "description": "Boolean true or false"
    },
    "email_field": {
      "type": "string",
      "format": "email",
      "description": "Valid email address"
    },
    "uuid_field": {
      "type": "string",
      "format": "uuid",
      "description": "Valid UUID v4"
    },
    "date_field": {
      "type": "string",
      "format": "date",
      "description": "Valid date (YYYY-MM-DD)"
    },
    "uri_field": {
      "type": "string",
      "format": "uri",
      "description": "Valid URI"
    },
    "optional_string": {
      "type": "string",
      "description": "An optional string field"
    }
  }
}

{
  "required_string": "Valid",
  "string_with_length": "This string is way too long for the maxLength constraint"
}

{
  "title": "Public Read Schema {{$timestamp}}",
  "description": "Schema with public read access for RBAC testing",
  "organisation": "{{org_uuid}}",
  "authorization": {
    "read": ["public"],
    "create": [],
    "update": [],
    "delete": []
  },
  "properties": {
    "title": {"type": "string", "required": true},
    "public_data": {"type": "string"},
    "content": {"type": "string"}
  }
}

{
  "title": "Public Test Object",
  "public_data": "This data should be publicly readable",
  "content": "Anyone can read this without authentication"
}
